import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Import the default styles
const Home = () => {
    const [users, setUsers] = useState([]);
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([]);

    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const userResponse = await axios.get('http://localhost:3000/api/users', {
                    headers: { Authorization: `Bearer ${token}` }


                });
                setUsers(userResponse.data);
                console.log("worked");
                const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFriends(friendResponse.data);
                console.log("response", friendResponse.data)
                const recommendationResponse = await axios.get('http://localhost:3000/api/recommendations', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                console.log("done")


                setRecommendations(recommendationResponse.data);
                console.log(recommendationResponse.data.username)
            } catch (error) {
                console.error('Error fetching data', error);
            }
        };
        fetchData();
    }, [token]); // Notice 'users' is not included here




    const addFriend = async (userId) => {
        try {
            // Send friend request
            const response = await axios.post(
                'http://localhost:3000/api/friend-request',
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            console.log('Friend request sent', response.data);
            toast.success("Friend request sent"); // Display error toast
            // Update state: Remove user from `users` and add to `friends`
            setUsers(users.filter(user => user._id !== userId));
            // Optionally, you may want to refetch the friends list or update it manually if needed
            const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setFriends(friendResponse.data);

        } catch (error) {
            console.error('Error sending friend request', error.response ? error.response.data : error.message);
        }
    };
    return (
        <div>
            <h2>All Users</h2>
            {users.length === 0 ? (
                <p>No users found</p>
            ) : (
                users.map(user => (
                    <div key={user._id}>
                        {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
                    </div>
                ))
            )}

            <h1>Friends List</h1>
            {friends.length === 0 ? (
                <p>You have no friends yet.</p>
            ) : (
                friends.map(friend => (
                    <div key={friend._id}>
                        {friend.username} <button>Unfriend</button>
                    </div>
                ))
            )}

            {/* 
            {users.length === 0 ? <p>No users found</p> : users.map(user => (
                <div key={user._id}>
                    {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
                </div>
            ))} */}
            <h1>Recommendations</h1>
            {recommendations.length === 0 ? <p>No recommendations found</p> : recommendations.map(rec => (
                <div key={rec._id}>
                    {rec.username} (Mutual Friends: {rec.mutualFriendsCount}) <button>Add Friend</button>
                </div>
            ))}
            <ToastContainer /> {/* Add this to show toast notifications */}
        </div>
    );
};

export default Home;
