import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Import the default styles

const Home = () => {
    const [users, setUsers] = useState([]);
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [friendRequests, setFriendRequests] = useState([]);


    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch users
                const userResponse = await axios.get('http://localhost:3000/api/users', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUsers(userResponse.data);


                // Fetch friends
                const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFriends(friendResponse.data);
                console.log("Friends response", friendResponse.data);

                // Fetch recommendations
                const recommendationResponse = await axios.get('http://localhost:3000/api/recommendations', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setRecommendations(recommendationResponse.data);
                console.log("Recommendations response", recommendationResponse.data);

                // Extract and set friend requests from the user data
                const requestResponse = await axios.get('http://localhost:3000/api/friend-requests', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFriendRequests(requestResponse.data);
                console.log("Friend Requests response", requestResponse.data);

            } catch (error) {
                console.error('Error fetching data', error);
            }
        };
        fetchData();
    }, [token]);

    const addFriend = async (userId) => {
        try {
            // Send friend request
            const response = await axios.post(
                'http://localhost:3000/api/friend-request',
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            console.log('Friend request sent', response.data);
            toast.success("Friend request sent");

            // Update state
            setUsers(users.filter(user => user._id !== userId));
            setRecommendations(recommendations.filter(rec => rec._id !== userId));

            // Refetch friends
            const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setFriends(friendResponse.data);

        } catch (error) {
            console.error('Error sending friend request', error.response ? error.response.data : error.message);
        }
    };
    const acceptRequest = async (userId) => {
        try {
            await axios.post(
                'http://localhost:3000/api/friend-request/accept',
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success("Friend request accepted");

            setFriendRequests(friendRequests.filter(request => request._id !== userId));

            const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setFriends(friendResponse.data);

        } catch (error) {
            console.error('Error accepting friend request', error.response ? error.response.data : error.message);
        }
    };

    const rejectRequest = async (userId) => {
        try {
            await axios.post(
                'http://localhost:3000/api/friend-request/reject',
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success("Friend request rejected");

            setFriendRequests(friendRequests.filter(request => request._id !== userId));

        } catch (error) {
            console.error('Error rejecting friend request', error.response ? error.response.data : error.message);
        }
    };

    //     return (
    //         <div>
    //             <h2>All Users</h2>
    //             {users.length === 0 ? (
    //                 <p>No users found</p>
    //             ) : (
    //                 users.map(user => (
    //                     <div key={user._id}>
    //                         {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
    //                     </div>
    //                 ))
    //             )}

    //             <h1>Friends List</h1>
    //             {friends.length === 0 ? (
    //                 <p>You have no friends yet.</p>
    //             ) : (
    //                 friends.map(friend => (
    //                     <div key={friend._id}>
    //                         {friend.username} <button>Unfriend</button>
    //                     </div>
    //                 ))
    //             )}

    //             <h1>Recommendations</h1>
    //             {recommendations.length === 0 ? <p>No recommendations found</p> : recommendations.map(rec => (
    //                 <div key={rec._id}>
    //                     {rec.username} (Mutual Friends: {rec.mutualFriendsCount}) <button onClick={() => addFriend(rec._id)}>Add Friend</button>
    //                 </div>
    //             ))}


    //             <h1>Friend Requests</h1>
    //             {friendRequests.length === 0 ? <p>No friend requests</p> : friendRequests.map(request => (
    //                 <div key={request._id}>
    //                     {request.username} <button onClick={() => acceptRequest(request._id)}>Accept</button> <button onClick={() => rejectRequest(request._id)}>Reject</button>
    //                 </div>
    //             ))}

    //             <ToastContainer />
    //         </div>
    //     );
    // };

    return (
        <div>
            <h2>All Users</h2>
            {users.length === 0 ? (
                <p>No users found</p>
            ) : (
                users.map(user => (
                    <div key={user._id} className="user-card">

                        <p>{user.username}</p>
                        <button onClick={() => addFriend(user._id)}>Follow</button>
                    </div>
                ))
            )}

            <h1>Friends List</h1>
            {friends.length === 0 ? (
                <p>You have no friends yet.</p>
            ) : (
                friends.map(friend => (
                    <div key={friend._id} className="friend-card">

                        <p>{friend.username}</p>
                        <button className="unfriend">Unfriend</button>
                    </div>
                ))
            )}

            <h1>Recommendations</h1>
            {recommendations.length === 0 ? (
                <p>No recommendations found</p>
            ) : (
                recommendations.map(rec => (
                    <div key={rec._id} className="recommendation-card">

                        <p>{rec.username}</p>
                        <button onClick={() => addFriend(rec._id)}>Follow</button>
                    </div>
                ))
            )}

            <h1>Friend Requests</h1>
            {friendRequests.length === 0 ? (
                <p>No friend requests</p>
            ) : (
                friendRequests.map(request => (
                    <div key={request._id} className="request-card">

                        <p>{request.username}</p>
                        <button onClick={() => acceptRequest(request._id)}>Accept</button>
                        <button onClick={() => rejectRequest(request._id)} className="reject">Reject</button>
                    </div>
                ))
            )}

            <ToastContainer />
        </div>
    );
};
