import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Import the default styles

const Home = () => {
    const [users, setUsers] = useState([]);
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [friendRequests, setFriendRequests] = useState([]);

    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch users
                const userResponse = await axios.get('http://localhost:3000/api/users', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUsers(userResponse.data);
                console.log("Users fetched", userResponse.data);
                // userResponse.data.forEach(user => {
                //     setFriendRequests(user.friendRequests);
                //     console.log("User:", user.friendRequests);
                // });

                // Fetch friends
                const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFriends(friendResponse.data);
                console.log("Friends response", friendResponse.data);

                // Fetch recommendations
                const recommendationResponse = await axios.get('http://localhost:3000/api/recommendations', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setRecommendations(recommendationResponse.data);
                console.log("Recommendations response", recommendationResponse.data);

                // Extract and set friend requests from the user data
                // Fetch friend requests
                const requestsResponse = await axios.get('http://localhost:3000/api/friend-requests', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                const friendRequestsData = await Promise.all(requestsResponse.data.map(async (requestId) => {
                    const userResponse = await axios.get(`http://localhost:3000/api/users/${requestId}`, {
                        headers: { Authorization: `Bearer ${token}` }
                    });
                    return userResponse.data;
                }));
                setFriendRequests(friendRequestsData);

            } catch (error) {
                console.error('Error fetching data', error);
            }
        };
        fetchData();
    }, [token]);

    const addFriend = async (userId) => {
        try {
            // Send friend request
            const response = await axios.post(
                'http://localhost:3000/api/friend-request',
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            console.log('Friend request sent', response.data);
            toast.success("Friend request sent");

            // Update state
            setUsers(users.filter(user => user._id !== userId));

            // Refetch friends
            const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setFriends(friendResponse.data);

        } catch (error) {
            console.error('Error sending friend request', error.response ? error.response.data : error.message);
        }
    };

    return (
        <div>
            <h2>All Users</h2>
            {users.length === 0 ? (
                <p>No users found</p>
            ) : (
                users.map(user => (
                    <div key={user._id}>
                        {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
                    </div>
                ))
            )}

            <h1>Friends List</h1>
            {friends.length === 0 ? (
                <p>You have no friends yet.</p>
            ) : (
                friends.map(friend => (
                    <div key={friend._id}>
                        {friend.username} <button>Unfriend</button>
                    </div>
                ))
            )}

            <h1>Recommendations</h1>
            {recommendations.length === 0 ? <p>No recommendations found</p> : recommendations.map(rec => (
                <div key={rec._id}>
                    {rec.username} (Mutual Friends: {rec.mutualFriendsCount}) <button>Add Friend</button>
                </div>
            ))}

            {/* Display friend requests for debugging */}
            <h1>Friend Requests</h1>
            {friendRequests.length === 0 ? (
                <p>No friend requests</p>
            ) : (
                friendRequests.map(request => (
                    <div key={request}>
                        {request.username} {/* Modify this based on actual data structure */}
                    </div>
                ))
            )}

            <ToastContainer />
        </div>
    );
};


export default Home;
