import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home = () => {
    const [users, setUsers] = useState([]);
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([]);

    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchData = async () => {
            try {
                const userResponse = await axios.get('http://localhost:3000/api/users', {
                    headers: { Authorization: `Bearer ${token}` }

                });
                console.log("user", userResponse.data);
                console.log("worked");
                setUsers(userResponse.data);
                const addFriend = async (userId) => {
                    try {
                        await axios.post(
                            'http://localhost:3000/api/friend-request',
                            {},  // Payload
                            { headers: { Authorization: `Bearer ${token}` } }
                        );
                        console.log('Friend request sent');
                    } catch (error) {
                        console.error('Error sending friend request', error);
                    }
                };


                const recommendationResponse = await axios.get('http://localhost:3000/api/recommendations', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                console.log("done")

                setFriends(addFriend.data);
                setRecommendations(recommendationResponse.data);
            } catch (error) {
                console.error('Error fetching data', error);
            }
        };
        fetchData();
    }, [token]); // Notice 'users' is not included here




    const addFriend = async (userId) => {
        try {
            await axios.post(
                `/api/friend-request`,
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
        } catch (error) {
            console.error('Error sending friend request', error);
        }
    };

    return (
        <div>
            <h2>All Users</h2>
            {users.length === 0 ? (
                <p>No users found</p>
            ) : (
                users.map(user => (
                    <div key={user._id}>
                        {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
                    </div>
                ))
            )}

            <h1>Friends List</h1>
            {friends.length === 0 ? (
                <p>You have no friends yet.</p>
            ) : (
                friends.map(friend => (
                    <div key={friend._id}>
                        {friend.username} <button>Unfriend</button>
                    </div>
                ))
            )}


            {users.length === 0 ? <p>No users found</p> : users.map(user => (
                <div key={user._id}>
                    {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
                </div>
            ))}

            {recommendations.length === 0 ? <p>No recommendations found</p> : recommendations.map(rec => (
                <div key={rec._id}>
                    {rec.username} (Mutual Friends: {rec.mutualFriendsCount}) <button>Add Friend</button>
                </div>
            ))}

        </div>
    );
};

export default Home;
