import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'; // Import the default styles
import { jwtDecode } from 'jwt-decode';
const Home = () => {
    const [users, setUsers] = useState([]);
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([]);
    const [friendRequests, setFriendRequests] = useState([]);
    const user = userResponse.data.find(u => u._id === userId);

    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Decode token to get user ID
                const decodedToken = jwtDecode(token);
                const userId = decodedToken._id; // Adjust according to your token structure

                // Fetch users
                const userResponse = await axios.get('http://localhost:3000/api/users', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUsers(userResponse.data);
                console.log("Users fetched", userResponse.data);

                // Ensure userResponse.data is an array
                if (Array.isArray(userResponse.data)) {
                    // Find the user with the userId
                    const user = userResponse.data.find(u => u._id === userId);
                    console.log("User found:", user);

                    if (user) {
                        // Extract unique friend requests
                        const uniqueFriendRequests = [...new Set(user.friendRequests)];
                        console.log("Unique Friend Requests:", uniqueFriendRequests);

                        // Set state
                        setFriendRequests(uniqueFriendRequests);
                    } else {
                        console.log("User with ID:", userId, "not found");
                    }
                } else {
                    console.error("User response data is not an array:", userResponse.data);
                }

                // Fetch friends
                const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFriends(friendResponse.data);
                console.log("Friends response", friendResponse.data);

                // Fetch recommendations
                const recommendationResponse = await axios.get('http://localhost:3000/api/recommendations', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setRecommendations(recommendationResponse.data);
                console.log("Recommendations response", recommendationResponse.data);

            } catch (error) {
                console.error('Error fetching data', error);
            }
        };
        fetchData();
    }, [token]);

    const addFriend = async (userId) => {
        try {
            // Send friend request
            const response = await axios.post(
                'http://localhost:3000/api/friend-request',
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            console.log('Friend request sent', response.data);
            toast.success("Friend request sent");

            // Update state
            setUsers(users.filter(user => user._id !== userId));

            // Refetch friends
            const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setFriends(friendResponse.data);

        } catch (error) {
            console.error('Error sending friend request', error.response ? error.response.data : error.message);
        }
    };

    // Function to get username by ObjectId
    const getUsernameById = (id) => {
        const user = users.find(user => user._id === id);
        return user ? user.username : 'Unknown';
    };

    return (
        <div>
            <h2>All Users</h2>
            {users.length === 0 ? (
                <p>No users found</p>
            ) : (
                users.map(user => (
                    <div key={user._id}>
                        {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
                    </div>
                ))
            )}

            <h1>Friends List</h1>
            {friends.length === 0 ? (
                <p>You have no friends yet.</p>
            ) : (
                friends.map(friend => (
                    <div key={friend._id}>
                        {friend.username} <button>Unfriend</button>
                    </div>
                ))
            )}

            <h1>Recommendations</h1>
            {recommendations.length === 0 ? <p>No recommendations found</p> : recommendations.map(rec => (
                <div key={rec._id}>
                    {rec.username} (Mutual Friends: {rec.mutualFriendsCount}) <button>Add Friend</button>
                </div>
            ))}

            <h1>Friend Requests</h1>
            {friendRequests.length === 0 ? (
                <p>No friend requests</p>
            ) : (
                friendRequests.map(requestId => (
                    <div key={requestId}>
                        {getUsernameById(requestId)} {/* Display username */}
                    </div>
                ))
            )}

            <ToastContainer />
        </div>
    );
};

export default Home;
