import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home = () => {
    const [users, setUsers] = useState([]);
    const [friends, setFriends] = useState([]);
    const [recommendations, setRecommendations] = useState([]);

    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch users
                const userResponse = await axios.get('http://localhost:3000/api/users', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUsers(userResponse.data);
                console.log("Users fetched:", userResponse.data);

                // Fetch friends
                const friendResponse = await axios.get('http://localhost:3000/api/friends', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFriends(friendResponse.data);
                console.log("Friends fetched:", friendResponse.data);

                // Fetch recommendations
                const recommendationResponse = await axios.get('http://localhost:3000/api/recommendations', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setRecommendations(recommendationResponse.data);
                console.log("Recommendations fetched:", recommendationResponse.data);

            } catch (error) {
                console.error('Error fetching data', error);
            }
        };

        fetchData();
    }, [token]);

    const addFriend = async (userId) => {
        try {
            const response = await axios.post(
                'http://localhost:3000/api/friend-request',
                { userId },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            console.log('Friend request sent', response.data);
            // Optionally, refresh friend list or provide user feedback
        } catch (error) {
            console.error('Error sending friend request', error.response ? error.response.data : error.message);
        }
    };

    return (
        <div>
            <h2>All Users</h2>
            {users.length === 0 ? (
                <p>No users found</p>
            ) : (
                users.map(user => (
                    <div key={user._id}>
                        {user.username} <button onClick={() => addFriend(user._id)}>Add Friend</button>
                    </div>
                ))
            )}

            <h1>Friends List</h1>
            {friends.length === 0 ? (
                <p>You have no friends yet.</p>
            ) : (
                friends.map(friend => (
                    <div key={friend._id}>
                        {friend.username} <button>Unfriend</button>
                    </div>
                ))
            )}

            <h2>Recommended Friends</h2>
            {recommendations.length === 0 ? (
                <p>No recommendations found</p>
            ) : (
                recommendations.map(rec => (
                    <div key={rec._id}>
                        {rec.username} (Mutual Friends: {rec.mutualFriendsCount}) <button onClick={() => addFriend(rec._id)}>Add Friend</button>
                    </div>
                ))
            )}
        </div>
    );
};

export default Home;
